mapper.insert:
insert into bus_car_package_audit (service_item_id, service_item_info, service_item_price, instance_id, 
  creator, auditor_id, bpmnInfo_id, info, status, audit_time, create_time
  )
values (#{serviceItemId}, #{serviceItemInfo}, #{serviceItemPrice}, #{instanceId}, 
  #{creator}, #{auditorId}, #{bpmnInfoId}, #{info}, #{status}, #{auditTime}, #{createTime}
  )

mapper.select:
select audit.id, audit.service_item_id, audit.service_item_info, audit.service_item_price, audit.instance_id, audit.creator, audit.auditor_id, audit.bpmnInfo_id, audit.info, audit.status, audit.audit_time, audit.create_time, user.user_name username, item.name item_name from bus_car_package_audit audit left join bus_service_item item on audit.service_item_id = item.id left join sys_user user on audit.auditor_id = user.id
AND audit.creator = #{creatorName}
AND audit.auditor_id = #{auditorId}
AND audit.status = #{status}
AND audit.info Like concat('%',#{name},'%')
